import { ChangeDetectorRef, Component, OnInit } from '@angular/core';
import { Router } from '@angular/router';
import { AppPaths, ClaimKey, Constant } from './constant/constant';
import { EmitterService } from './core/emitter.service';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']
})
export class AppComponent implements OnInit {
  isLoading: boolean = false;
  isLogined: boolean = false;
  appPaths = AppPaths;
  logggedInUserName: string = 'Logged In';

  isMobileNavVisible: boolean = false;
  isCompact: boolean = false;
  Menus = [
    {
      label: 'User',
      icon: 'fas fa-photo-video',
      isOpen: false,
      items: [{ menu: 'User List', path: AppPaths.userList }, { menu: 'Add New User', path: AppPaths.addEditUser }],
    },
    {
      label: 'Role',
      icon: 'fas fa-photo-video',
      isOpen: false,
      items: [{ menu: 'Role List', path: AppPaths.roleList }, { menu: 'Add New Role', path: AppPaths.addEditRole }],
    }
  ];

  constructor(private emitService: EmitterService, private cdr: ChangeDetectorRef, private router: Router) {
    this.emitService.loaderEmitter.subscribe(loader => {
      this.isLoading = loader;
      this.cdr.detectChanges();
    });
    this.emitService.loginEmitter.subscribe(us => {
      this.isLogined = us;
      this.logggedInUserName = localStorage.getItem(ClaimKey.firstName) + ' ' + localStorage.getItem(ClaimKey.lastName)
      this.cdr.detectChanges();
    });
    this.logggedInUserName = localStorage.getItem(ClaimKey.firstName) + ' ' + localStorage.getItem(ClaimKey.lastName)

  }

  ngOnInit() {
    let logined = localStorage.getItem(ClaimKey.isLoggedIn) == Constant.true;
    this.isLogined = logined;
    let currentPage = this.router.url;
    if (!this.isLogined) {
      this.router.navigate(['/' + AppPaths.login]);
    }
    else if (currentPage && currentPage == '/' || currentPage.includes(AppPaths.login)) {
      this.router.navigate(['/' + AppPaths.home]);
    }


  }
  
  toggleDropdown(dropdown: any): void {
    this.Menus.forEach((d) => {
      if (d !== dropdown) {
        d.isOpen = false;
      }
    });
    dropdown.isOpen = !dropdown.isOpen;
  }

}
<body>
  <div class="dashboard" *ngIf="isLogined">
    <div class="dashboard-nav" [class.mobile-show]="isMobileNavVisible">
      <header>
        <a href="#" class="brand-logo">
          <i class="fas fa-anchor"></i> <span>Banking Tool</span>
        </a>
      </header>
      <nav class="dashboard-nav-list">
        <a [routerLink]="['/'+appPaths.home]" class="dashboard-nav-item">
          <i class="fas fa-home"></i> Home
        </a>
        <div class="dashboard-nav-dropdown"
             *ngFor="let dropdown of Menus"
             [class.show]="dropdown.isOpen">
          <a class="dashboard-nav-item dashboard-nav-dropdown-toggle"
             (click)="toggleDropdown(dropdown)">
            <i [class]="dropdown.icon"></i> {{ dropdown.label }}
          </a>
          <div class="dashboard-nav-dropdown-menu">
            <a *ngFor="let item of dropdown.items"
               class="dashboard-nav-dropdown-item"
               [routerLink]="['/'+item.path]">
              {{ item.menu }}
            </a>
          </div>
        </div>
        <div class="nav-item-divider"></div>
        <a href="#" class="dashboard-nav-item">
          <i class="fas fa-sign-out-alt"></i> Logout
        </a>
      </nav>
    </div>
    <div class="dashboard-app" [class.dashboard-compact]="isCompact">
      <header class="dashboard-toolbar">
        <div class="container">
          <div class="row justify-content-end">
            <div class="col-2">
              {{logggedInUserName}}
            </div>
          </div>
        </div>
      </header>
      <div class="dashboard-content">
        <router-outlet></router-outlet>
      </div>
    </div>
  </div>

  <div *ngIf="!isLogined">
    <router-outlet></router-outlet>
  </div>
  <app-loader *ngIf="isLoading"></app-loader>
</body>




